import React, { Component } from 'react';
import {Col, Row, Jumbotron, Container, Card} from 'react-bootstrap';

import vasoFront from '../images/vasoFront.jpg';

class Header extends Component {
  
  render() {
    
    return (
      <Card className="text-white header container-fluid p-0 border-0">
        <Card.Img src={vasoFront} alt="Vaso" className="bg-header" />
        <Card.ImgOverlay className="p-0 mt-50 header-wrapper">
          <div className="header-div p-30">
            <Card.Title><h1>Vasiliki Vourliotaki</h1></Card.Title>
            <Card.Text>
              <p>Chef de Cuisine<br/>Innovative approach to Greek-inspired Mediterranean cuisine </p>
            </Card.Text>
          </div>
        </Card.ImgOverlay>
      </Card>   
    )
  }
}

export default Header;


