import React from 'react';

const BlockQuote = () => (
    <div className="card">
  <div className="card-body">
    <blockquote className="blockquote mb-0">
      <p>“...My hope is for guests to indulge in Greek food with some familiarity, thanks to American ingredients that help them connect to, and understand, each dish...” </p>
      <footer className="blockquote-footer"><cite title="Source Title">Vasiliki Vourliotaki</cite></footer>
    </blockquote>
  </div>
</div>

)

export default BlockQuote;
